    // Lea->Jane line
    setTimeout(() => {
      setShowLeaJane(!showLeaJane);
    }, 15700);

    // Jane Node
    setTimeout(() => {
      setShowJaneCollapse(false);
      setShowJaneAnimation(!showJaneAnimation);
    }, 15900);

    // Jane->Sam line (glows the line)
    setTimeout(() => {
      setShowJaneSam(true);
    }, 16700);

    // Sam Node (Sam)
    setTimeout(() => {
      setShowSamCollapse(false);
      setShowSamAnimation(!showSamAnimation); // true
    }, 16900);

    // Sam->SamsLeftNone line
    setTimeout(() => {
      setShowSamSamsLeftNone(!showSamSamsLeftNone);
    }, 17400);

    // SamsLeftNone (SamsLeftNone)
    setTimeout(() => {
      setShowSamsLeftNoneCollapse(false);
      setShowSamsLeftNoneAnimation(!showSamsLeftNoneAnimation);
      setShowSamsLeftNoneValue(true);
      setTimeout(() => {
        setShowSamsLeftNoneAnimation(false);
      }, 1500);
      // SamSamsLeftNone line (SamSamsLeftNone line)
      setTimeout(() => {
        setShowSamSamsLeftNone(false);
      }, 2700);
    }, 17500);

    // Sam->SamsRightNone
    setTimeout(() => {
      setShowSamSamsRightNone(!showSamSamsRightNone); // true
    }, 20600);

    // SamsRightNone
    setTimeout(() => {
      setShowSamsRightNoneCollapse(false);
      setShowSamsRightNoneAnimation(!showSamsRightNoneAnimation);
      setShowSamsRightNoneValue(true);
      setTimeout(() => {
        setShowSamsRightNoneAnimation(false);
      }, 1500);
      // SamSamsRightNone line (stop glowing)
      setTimeout(() => {
        setShowSamSamsRightNone(false);
      }, 2800);
    }, 20700);

    // Sam (turn off)
    setTimeout(() => {
      setShowSamValue(true);
      setShowSamAnimation(false);
    }, 24100);

    // Sam-Jane line (stop glowing)
    setTimeout(() => {
      setShowJaneSam(false);
    }, 25500);

    // Jane->LeftNull line
    setTimeout(() => {
      setShowJaneNull(!showJaneNull); // true
    }, 26000);

    // RightNull Node
